package paquete;

public class Final {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final int LUNA = 238857; //variables constantes final se acostumbra que sean mayusculas
		System.out.println("Distancia a la luna " +LUNA+ "millas");
		double lunaKilo;
		lunaKilo = LUNA*1.609;
		System.out.println("En kilometros es " +lunaKilo+ "km");
	}

}
